package katas.kotlin.leetcode.integer_to_roman

import kotlincommon.test.shouldEqual
import org.junit.Test

/**
 * https://leetcode.com/problems/integer-to-roman
 * https://leetcode.com/problems/roman-to-integer
 */
class IntegerToRomanTests {
    private val examples = listOf(
        1 to "I",
        2 to "II",
        3 to "III",
        4 to "IV",
        5 to "V",
        6 to "VI",
        7 to "VII",
        8 to "VIII",
        9 to "IX",
        10 to "X",
        11 to "XI",
        12 to "XII",
        40 to "XL",
        50 to "L",
        58 to "LVIII",
        59 to "LIX",
        89 to "LXXXIX",
        90 to "XC",
        100 to "C",
        400 to "CD",
        500 to "D",
        900 to "CM",
        1000 to "M",
        1994 to "MCMXCIV",
        50_000_000 to ""
    )

    @Test fun `convert number to numeral`() {
        examples.forEach { (number, numeral) ->
            number.toRoman() shouldEqual numeral
        }
    }

    @Test fun `convert numeral to number`() {
        examples.take(10).forEach { (number, numeral) ->
            numeral.romanToInt() shouldEqual number
        }
    }
}

private fun String.romanToInt(): Int {
    if (this == "") return 0
    var s = this
    var result = 0
    while (s != "") {
        val (numeral, number) = numberByNumeral.entries.find { s.startsWith(it.key) }!!
        result += number
        s = s.drop(numeral.length)
    }
    return result
}

private val numeralByNumber = mapOf(
    1000 to "M", 900 to "CM",
    500 to "D", 400 to "CD",
    100 to "C", 90 to "XC",
    50 to "L", 40 to "XL",
    10 to "X", 9 to "IX",
    5 to "V", 4 to "IV",
    1 to "I"
)

private val numberByNumeral = numeralByNumber.entries.associate { it.value to it.key }

private fun Int.toRoman(): String {
    if (this <= 0) error("")
    val result = StringBuilder()
    var n = this
    while (n != 0) {
        val (number, numeral) = numeralByNumber.entries.find { n >= it.key }!!
        result.append(numeral)
        n -= number
    }
    return result.toString()
}
